import React, { Component } from 'react';
import axios from 'axios';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: "",
            surname: "",
            Email: "",
            userGroup: "",
            subscribeto: ""
        }
    }

    handleChange(e, textFor) {
        const text = e.target.value;
        switch (textFor) {
            case "userName":
                this.setState({ userName: text });
                break;
            case "surname":
                this.setState({ surname: text });
                break;
            case "Email":
                this.setState({ Email: text });
                break;
            case "userGroup":
                this.setState({ userGroup: text });
                break;
            case "subscribeto":
                this.setState({ subscribeto: text });
                break;
        }
    }

    handleSubmit = (event) => {
        const { userName, surname, Email, userGroup, subscribeto } = this.state;

        axios
            .post(
                "http://localhost:3000/users/:name",
                {
                    user: {
                        name: userName,
                        surname,
                        Email,
                        userGroup,
                        subscribeto
