import React, { Component } from 'react';
import $ from "jquery";
import { LitElement, html } from 'lit-element';
import '@fortawesome/fontawesome-free/css/all.css';

const CSS = {
  loggeduser: 'loggeduser',
  menu: 'menu'
}

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: this.props.users,
      openMenu: false,
      userProfile: "hide"
    };
    this.openMenu = this.openMenu.bind(this);
    this.userProfile = this.userProfile.bind(this);
  }

  openMenu() {
    if (this.state.openMenu === false) {
      this.setState({ openMenu: !this.state.openMenu });
    }
    else {
      this.setState({openMenu : !this.state.openMenu})
    } 
  }

  userProfile() {
    if (this.state.userProfile === "hide") {
      this.setState({ userProfile: "show", openMenu : !this.state.openMenu });
    }
    else {
      this.setState({userProfile : "hide"})
    } 
  }
  /* componentDidMount() {
    let users = [];
    const url = "http://localhost:3000/users";

    fetch(url)
    .then(response => response.json())
      .then(res => {
          console.log(res.users)
        $.each(res.users, function(index, value) {
          users.push({
            id: value.id, 
            name: value.name 
          });
        });
        this.setState(() => ({ users }));

    }).catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
}   */

  render() {
    let { users } = this.state;
    let user, profile = "";

    if (users !== undefined) {
      user = users.map((value, index) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}> 
            </i>
            {this.state.openMenu ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={this.userProfile}>UserProfile</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
            {this.state.userProfile === "show" ? 
            <table>
              <tbody>
                <tr key={index + value.name} className="row m-2">
                    <td>
                      <input type="text" value={value.name} readOnly />
                    </td>
                </tr>
                <tr key={index+ value.s} className="row m-2">
                    <td>
                      <input type="text" value={value.surname} readOnly />
                    </td>
                </tr>
                <tr key={index} className="row m-2">
                    <td>
                      <input type="text" value={value.Email} readOnly />
                    </td>
                </tr>
                <tr key={index} className="row m-2">
                    <td>
                      <input type="text" value={value.userGroup} readOnly />
                    </td>
                </tr>
              </tbody>
              </table> : null
            }
          </div>
        )
      });
    }


    return (
      <div className={CSS.loggeduser}>
        {user}

      </div>
    );
  }
}

export default Users;
