        this.setState(() => ({ users }));

    }).catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
}  */

  render() { 
    let { users } = this.state;
    const name = useParams()
    let usersList = "";
    if (users !== undefined) {
      usersList = users.map((value) => {
        return (
          <li key={value.id}>
            {value.name}
          </li>
        )
      });       
    }
    return (
      <div>{usersList}</div>
    );
    }  
}

export default Users;
