import React, { Component } from 'react';
import $ from "jquery";

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users : this.props.users
    };


}
  componentDidMount() {
    let users = [];
    const url = "http://localhost:3000/users";

    axios
          .post(
            "http://localhost:3000/users",
            {
              user: {
                name: username,
                password: password
              }
            },
            {credentials: 'include'},
            { withCredentials: true }
          )
          .then(response => {
            let name = users.map((value) => value).filter(value => value.name === username && value.password === password);

              this.props.handleLogin(name);
              this.props.history.push("/users");
          })
          .catch(error => {
            console.log("login error", error);
          });
    event.preventDefault();
  }  

  render() { 
    let { users } = this.state;
    let usersList = "";
    if (users !== undefined) {
      usersList = users.map((value) => {
        return (
          <li key={value.id}>
            {value.name}
          </li>
        )
      });       
    }
    return (
      <div>{usersList}</div>
    );
    }  
}

export default Users;
