import React, { Component } from 'react';
import $ from "jquery";

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users : this.props.users
    };


}
  componentDidMount() {
    let users = [];
    const url = "http://localhost:3000/users";

    fetch(url)
    .then(response => response.json())
      .then(res => {
          console.log(res.users)
        $.each(res.users, function( value) {
          users.push({
            id: value.id, 
            name: value.name 
          });
        });
        this.setState(() => ({ users }));

    }).catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
} 

  render() { 
    let { users } = this.state;
    let usersList = users.map((value) => {
      return (
        <li key={value.id}>
          {value.name}
        </li>
      )
    }); 
        return (
          <div>{usersList}</div>
        );
    }
}

export default Users;
