import React, { Component } from 'react';
import $ from 'jquery';

class UpdateUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userId: this.props.userId,
            userList: this.props.userList,
            userName: this.props.userDetail.name,
            surname: this.props.userDetail.surname,
            Email: this.props.userDetail.Email,
            userGroup: this.props.userDetail.userGroup,
            subscribeto: this.props.userDetail.subscribeto,
        }
    }

    componentDidMount() {
        this.eventType();
    }

    eventType() {
        let eventtype = [];
        const urleventtype = "http://localhost:3000/eventtype";

        fetch(urleventtype)
            .then(response => response.json())
            .then(res => {
                $.each(res.eventtype, function (index, value) {
                    eventtype.push({
                        id: value.id,
                        name: value.name
                    });
                });

                this.setState(() => ({ eventtype }));
            }).catch(() => console.log("Can’t access " + urleventtype + " response. Blocked by browser?"));
    }

    render() {
        const { userName, surname, Email, userList, userId, userDetail, eventtype } = this.state;
        let subscribeToDom = "";
        if (eventtype !== undefined) {
            subscribeToDom = eventtype.map((item) => {
                return (
                    <option key={item.id} value={item.id}>{item.name}</option>
                )
            })
        }

        return (
            <div className="container">
                <form onSubmit={this.handleSubmit}>
                    <div className="row d-flex justify-content-end">
                        <button
                            type="submit"
                            className="btn btn-success m-2">
                            Submit
                    </button>
                    </div>
                    <div className="row">
                        <table className="table table-borderless usertable">
                            <tbody>
                                <tr>
                                    <td >
                                        User Name :
                                </td>
                                    <td>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={userName}
                                            onChange={(e) => this.handleChange(e, "userName")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        User Surname :
                                </td>
                                    <td>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={surname}
                                            onChange={(e) => this.handleChange(e, "surname")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        User Email :
                                </td>
                                    <td>
                                        <input
                                            type="email"
                                            className="form-control"
                                            value={Email}
                                            onChange={(e) => this.handleChange(e, "Email")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr >
                                    <td>
                                        User Group :
                                </td>
                                    <td>
                                        <select id="userGroup" className="browser-default custom-select">
                                            <option value="admin">Admin</option>
                                            <option value="user" defaultValue>User</option>
                                        </select>
                                    </td>
                                </tr>
                                <tr >
                                    <td>
                                        Subscribe To :
                                 </td>
                                    <td>
                                        <select id="subscribeTo" className="browser-default custom-select" multiple>
                                            {subscribeToDom}
                                        </select>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </form>
            </div>
        );
    }
}

export default UpdateUser;
