import React, { Component } from 'react';
import $ from 'jquery';

class Events extends Component {
    constructor(props) {
        super(props);
        this.state = {
            usersubscribeTo: this.props.value.subscribeTo
        }
    }

    componentDidMount() {
        let events = [];
        let eventtype = [];
        const urlevents = "http://localhost:3000/events";
        const urleventtype = "http://localhost:3000/eventtype";

        fetch(urleventtype)
            .then(response => response.json())
            .then(res => {
                $.each(this.state.usersubscribeTo, function (i, val) {
                    $.each(res.eventtype, function (index, value) {
                        if (val.toLowerCase() === value.id.toLowerCase()) {
                            eventtype.push({
                                id: value.id,
                                name: value.name
                            });
                        }
                    });
                })
                this.setState(() => ({ eventtype }));
            }).catch(() => console.log("Can’t access " + urleventtype + " response. Blocked by browser?"));


        fetch(urlevents)
            .then(response => response.json())
            .then(res => {
                $.each(this.state.eventtype, function (i, val) {
                    $.each(res.events, function (index, value) {
                        if (val.name.toLowerCase() === value.name.toLowerCase()) {
                            events.push({
                                id: value.id,
                                name: value.name,
                                description: value.description,
                                start: value.start,
                                end: value.end,
                                location: value.location
                            });
                        }
                    });
                })
                this.setState(() => ({ events }));
            }).catch(() => console.log("Can’t access " + urlevents + " response. Blocked by browser?"));
    }

    render() {
        console.log(this.state.events);
        let { events } = this.state;
        let eventList ;
        if (events !== undefined) {
            eventList = events.map(item => {
                return (
                    <table className="table table-borderless">
                    <thead>
                        <th><label>Event Type </label> </th>
                        <th><label>Event Description </label> </th>
                        <th><label>Event Start </label> </th>
                        <th><label>Event End </label> </th>
                        <th><label>Event Location</label> </th>
                    </thead>
                    <tbody>
                        <tr key={item.name} >
                            <td>
                                <input type="text" className="form-control" value={item.name} readOnly />
                            </td>
                            <td>
                                <input type="text" className="form-control" value={item.description} readOnly />
                            </td>
                            <td>
                                <input type="text" className="form-control" value={item.Start} readOnly />
                            </td>
                            <td>
                                <input type="text" className="form-control" value={item.end} readOnly />
                            </td>
                            <td>
                                <input type="text"  className="form-control"  value={item.location}  readOnly />
                            </td>
                        </tr>
                    </tbody>
                    </table>
                )
            })
        }

        }
    }

export default Events;
