import React, { Component } from 'react';
import $ from "jquery";
import { LitElement, html } from 'lit-element';
import '@fortawesome/fontawesome-free/css/all.css';
import UserProfile from "./userProfile";
import Events from "./events";
import AdminUser from "./adminUser"

const CSS = {
  loggeduser: 'loggeduser',
  menu: 'menu',
  profile: 'profile'
}

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: this.props.users,
      userList: this.props.userList,
      openMenu: "hide",
      userProfile: "hide",
      eventList: "show",
      magnageUser: "hide"
    };
    this.openMenu = this.openMenu.bind(this);
    this.userProfile = this.userProfile.bind(this);
    this.eventList = this.eventList.bind(this);
    this.manageUser = this.manageUser.bind(this);
  }

  openMenu() {
    if (this.state.openMenu === "hide") {
      this.setState({ openMenu: "show" });
    }
    else {
      this.setState({ openMenu: "hide" })
    }
  }

  userProfile() {
    if (this.state.userProfile === "hide") {
      this.setState({ userProfile: "show", openMenu: "hide", eventList: "hide" });
    }
  }

  eventList() {
    if (this.state.eventList === "hide") {
      this.setState({ eventList: "show", userProfile: "hide", openMenu: "hide" });
    }
  }

  manageUser() {
    if (this.state.magnageUser === "hide") {
      this.setState({ magnageUser: "show", eventList: "hide", userProfile: "hide", openMenu: "hide" });
    }
  }

  render() {
    let { users, userList } = this.state;
    let user, profile = "";

    if (users !== undefined) {
      user = users.map((value, index) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}>
            </i>
            {this.state.openMenu === "show" ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={this.userProfile}>UserProfile</li>
                <li key="eventlist" onClick={this.eventList}>Show Events</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
            {value.userGroup.toLowerCase() === "admin" ?
              <div className="container">
                <div className="row m-4">
                  <button
                    type="submit"
                    className="btn btn-success"
                    onClick={this.manageUser}
                  >Manage User</button>
                </div>
              </div> : null
            }
            {}
            {this.state.userProfile === "show" ?
              <UserProfile value={value} index={index} /> : null
            }
            {this.state.eventList === "show" ?
              <Events value={value} index={index} /> : null
            }

          </div>
        )
      });
    }
    return (
      <div className={CSS.loggeduser}>
        {user}
      </div>
    );
  }
}

export default Users;
