import React, { Component } from 'react';
import $ from "jquery";

class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
          email: "",
          password: "",
          loginErrors: ""
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
      }

    componentDidMount() {
        let users = [];
        //const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const url = "http://localhost:3000/users";

        fetch(url)
        .then(response => response.json())
        .then(res => {
            $.each(res.users, function(index, value) {
              users.push({
                id: value.id, 
                name: value.name 
              });
            });
            this.setState(() => ({users}));
        })

      .catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
    }

    render() { 
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="container">
                        <div className="row">
                            <div className="s-6 m-4 l-4 xl-4">
                                <label placeholder="Enter Username" required>User Name</label>
                            </div>
                            <div className="s-6 m-4 l-8 xl-8">
                                <input name= "username" className="form-control text-center" type="text" />
                            </div>
                        </div>
                        <div className="row">
                            <div className="s-6 m-4 l-4 xl-4">   
                                <label placeholder="Enter Password" required>Passowrd</label>
                            </div> 
                            <div className="s-6 m-4 l-8 xl-8">
                                <input name="userpass" className="form-control text-center" type="password" />  
                            </div>
                        </div>
                        <div className="row">
                            <button type="submit" className="btn btn-success">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Login;
