import React, { Component } from 'react';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: "",
            surname: "",
            Email: "",
            userGroup: "",
            subscribeto: ""
        }
    }

    handleChange(e, textFor) {
        const text = e.target.value;
        switch (textFor) {
            case "userName":
                this.setState({ userName: text });
                break;
            case "surname":
                this.setState({ surname: text });
                break;
            case "Email":
                this.setState({ Email: text });
                break;
            case "userGroup":
                this.setState({ userGroup: text });
                break;
            case "subscribeto":
                this.setState({ subscribeto: text });
                break;
        }
    }

    handleSubmit = (event) => {
        const { userName, surname, Email, userGroup, subscribeto } = this.state;

        axios
            .post(
                "http://localhost:3000/users",
                {
                    user: {
                        name: userame,
                        surname: surname,

                    }
                },
                { credentials: 'include' },
                { withCredentials: true }
            )
            .then(response => {
                let loginUser = users.map((value) => value).filter(value => value.name === username && value.password === password);
                let params = loginUser[0].name;
                this.props.handleLogin(loginUser, users);
                this.props.history.push("/users/" + params);
            })
            .catch(error => {
                console.log("login error", error);
            });
        event.preventDefault();
    }


    render() {
        return (
            <div className="container">
                <table className="table table-borderless usertable">
                    <tbody>
                        <tr>
                            <td >
                                User Name :
                        </td>
                            <td>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.userName}
                                    onChange={(e) => this.handleChange(e, "userName")}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td>
                                User Surname :
                        </td>
                            <td>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.surname}
                                    onChange={(e) => this.handleChange(e, "surname")}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td>
                                User Email :
                        </td>
                            <td>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.Email}
                                    onChange={(e) => this.handleChange(e, "Email")}
                                />
                            </td>
                        </tr>
                        <tr >
                            <td>
                                User Group :
                        </td>
                            <td>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.userGroup}
                                    onChange={(e) => this.handleChange(e, "userGroup")}
                                />
                            </td>
                        </tr>
                        <tr >
                            <td>
                                Subscribe To :
                        </td>
                            <td>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.subscribeto}
                                    onChange={(e) => this.handleChange(e, "subscribeto")}
                                />
                            </td>
                        </tr>
                    </tbody>
                </table>
                <button
                    type="submit"
                    className="btn btn-success m-2"
                    onClick={(e) => this.handleSubmit(e)}>
                    Submit
                </button>
            </div>
        );
    }
}

export default AddUser;
