import React, { Component } from 'react';
import $ from "jquery";
import { LitElement, html } from 'lit-element';
import '@fortawesome/fontawesome-free/css/all.css';
import UserProfile from "./userProfile";
import Events from "./eve";

const CSS = {
  loggeduser: 'loggeduser',
  menu: 'menu',
  profile: 'profile'
}

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: this.props.users,
      openMenu: false,
      userProfile: "hide",
      eventList: "show"
    };
    this.openMenu = this.openMenu.bind(this);
    this.userProfile = this.userProfile.bind(this);
    this.eventList = this.eventList.bind(this);
    this.subscribe = this.subscribe.bind(this);
  }

  openMenu() {
    if (this.state.openMenu === false) {
      this.setState({ openMenu: !this.state.openMenu });
    }
    else {
      this.setState({ openMenu: !this.state.openMenu })
    }
  }

  userProfile() {
    if (this.state.userProfile === "hide") {
      this.setState({ userProfile: "show", openMenu: !this.state.openMenu });
    }
  }

  eventList() {
    if (this.state.eventList === "hide") {
      this.setState({ eventList: "show", userProfile: "hide", openMenu: !this.state.openMenu });
    }
  }

  subscribe(data) {
    <div>
        {data
          .map(res => <span>{res}</span>)
          .reduce((prev, curr) => [prev, ', ', curr])}
     </div>
  }
  /* componentDidMount() {
    let users = [];
    const url = "http://localhost:3000/users";

    fetch(url)
    .then(response => response.json())
      .then(res => {
          console.log(res.users)
        $.each(res.users, function(index, value) {
          users.push({
            id: value.id, 
            name: value.name 
          });
        });
        this.setState(() => ({ users }));

    }).catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
}   */

  render() {
    let { users } = this.state;
    let user, profile = "";

    if (users !== undefined) {
      user = users.map((value, index) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}>
            </i>
            {this.state.openMenu ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={this.userProfile}>UserProfile</li>
                <li key="eventlist" onClick={this.eventList}>Show Events</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
            {this.state.userProfile === "show" ?
              <UserProfile value={value} index={index}/> : null
            }
            <Events />
          </div>
        )
      });
    }


    return (
      <div className={CSS.loggeduser}>
        {user}
      </div>
    );
  }
}

export default Users;
