import React, { Component } from 'react';
import axios from 'axios';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: "",
            surname: "",
            Email: "",
            userGroup: "",
            subscribeto: ""
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e, textFor) {
        const ele = e.target.value;
        switch (textFor) {
            case "userName":
                this.setState({ userName: ele });
                break;
            case "surname":
                this.setState({ surname: ele });
                break;
            case "Email":
                this.setState({ Email: ele });
                break;
            case "userGroup":
                this.setState({ userGroup: ele.options[ele.selectedIndex].text });
                break;
            case "subscribeto":
                this.setState({ subscribeto: ele.options[ele.selectedIndex].text });
                break;
        }
    }

    handleSubmit = (event) => {
        const { userName, surname, Email, userGroup, subscribeto } = this.state;

        axios
            .post(
                "http://localhost:3000/users/:name",
                {
                    name: userName,
                    surname,
                    Email,
                    userGroup,
                    subscribeto
                },
                { credentials: 'include' },
                { withCredentials: true }
            )
            .then(response => { })
            .catch(error => {
                console.log("login error", error);
            });
        event.preventDefault();
    }


    render() {
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit}>
                    <div className="row d-flex justify-content-end">
                        <button
                            type="submit"
                            className="btn btn-success m-2">
                            Submit
                    </button>
                    </div>
                    <div className="row">
                        <table className="table table-borderless usertable">
                            <tbody>
                                <tr>
                                    <td >
                                        User Name :
                                </td>
                                    <td>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={this.state.userName}
                                            onChange={(e) => this.handleChange(e, "userName")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        User Surname :
                                </td>
                                    <td>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={this.state.surname}
                                            onChange={(e) => this.handleChange(e, "surname")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        User Email :
                                </td>
                                    <td>
                                        <input
                                            type="email"
                                            className="form-control"
                                            value={this.state.Email}
                                            onChange={(e) => this.handleChange(e, "Email")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr >
                                    <td>
                                        User Group :
                                </td>
                                    <td>
                                        <select id="userGroup" className="browser-default custom-select"
                                            onChange={(e) => this.handleChange(e, "userGroup")}>
                                            <option value="admin">Admin</option>
                                            <option value="user" selected>User</option>
                                        </select>
                                    </td>
                                </tr>
                                <tr >
                                    <td>
                                        Subscribe To :
                                 </td>
                                    <td>
                                        <select id="SubscribeTo" className="browser-default custom-select" multiple
                                        onChange={(e) => this.handleChange(e, "userGroup")}>
                                            <option value="admin">Admin</option>
                                            <option value="user" selected>User</option>
                                        </select>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </form>
            </div>
        );
    }
}

export default AddUser;
