}   */

  render() {
    let { users } = this.state;
    let user, profile = "";

    if (users !== undefined) {
      user = users.map((value) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}> 
            </i>
            {this.state.openMenu ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={this.userProfile}>UserProfile</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
          </div>
        )
      });
    }

    if (users !== undefined) {
      user = users.map((value) => {
        return (
          <table>
            <td>
              <input > </input>
