import React, { Component } from 'react';
import $ from 'jquery';

class UpdateUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userId: this.props.userId,
            userList: this.props.userList,
            userName: "",
            surname: "",
            Email: "",
            userGroup: "",
            subscribeto: "",
        }
    }

    componentDidMount() {
        const { userId, userList } = this.state;
        userList.map((value) => {
            if (parseInt(userId) === parseInt(value.id)) {
                this.setState({
                    userName: value.name,
                    surname: value.surname,
                    Email: value.Email,
                    userGroup: value.userGroup,
                    subscribeto: value.subscribeTo,
                })
            }
        })
        this.eventType();
    }

    eventType() {
        let eventtype = [];
        const urleventtype = "http://localhost:3000/eventtype";

        fetch(urleventtype)
            .then(response => response.json())
            .then(res => {
                $.each(res.eventtype, function (index, value) {
                    eventtype.push({
                        id: value.id,
                        name: value.name
                    });
                });

                this.setState(() => ({ eventtype }));
            }).catch(() => console.log("Can’t access " + urleventtype + " response. Blocked by browser?"));
    }


    render() {
        const { userName, surname, Email, subscribeto, userGroup, eventtype } = this.state;

        let subscribeToDom = "";
        if (eventtype !== undefined) {
            $
            subscribeToDom = eventtype.map((item) => {
                if (item.id === value.id) {
                    return (
                        <option key={item.id} value={item.id}>{item.name}</option>
                    )
                }
                else {
                    return (
                        <option key={item.id} value={item.id}>{item.name}</option>
                    )
                }

            })
        }

        return (
            <div className="container">
                <form onSubmit={this.handleSubmit}>
                    <div className="row d-flex justify-content-end">
                        <button
                            type="submit"
                            className="btn btn-success m-2">
                            Submit
                    </button>
                    </div>
                    <div className="row">
                        <table className="table table-borderless usertable">
                            <tbody>
                                <tr>
                                    <td >
                                        User Name :
                                </td>
                                    <td>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={userName}
                                            onChange={(e) => this.handleChange(e, "userName")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        User Surname :
                                </td>
                                    <td>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={surname}
                                            onChange={(e) => this.handleChange(e, "surname")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        User Email :
                                </td>
                                    <td>
                                        <input
                                            type="email"
                                            className="form-control"
                                            value={Email}
                                            onChange={(e) => this.handleChange(e, "Email")}
                                            required
                                        />
                                    </td>
                                </tr>
                                <tr >
                                    <td>
                                        User Group :
                                </td>
                                    <td>
                                        <select value={userGroup} id="userGroup" className="browser-default custom-select">
                                            <option value="admin">Admin</option>
                                            <option value="user">User</option>
                                        </select>
                                    </td>
                                </tr>
                                <tr >
                                    <td>
                                        Subscribe To :
                                 </td>
                                    <td>
                                        <select id="subscribeTo" className="browser-default custom-select" multiple>
                                            {subscribeToDom}
                                        </select>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </form>
            </div>
        );
    }
}

export default UpdateUser;
