import React, { Component } from 'react';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName : "",
            surname: "",
            Email: "",
            userGroup: "",
            subscribeto: ""
        }
    }

    handleChange(e, textFor) {
        const text = e.target.value;


        switch (textFor) {
            case "userName":
                this.setState({ userName: text });
                break;
            case "surname":
                this.setState({ surname: text });
                break;
            case "manageUser":
              this.setState({
                manageUser: "show",
                eventList: "hide",
                userProfile: "hide",
                addUser: "hide"
              });
              break;
            case "addUser":
              this.setState({
                addUser: "show",
                eventList: "hide",
                userProfile: "hide",
                manageUser: "hide"
              });
              break;
          }
    }


    render() {
        return (
            <table className="table table-borderless usertable">
                <tbody>
                    <tr>
                        <td >
                            User Name :
                        </td>
                        <td>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.userName}
                                onChange={(e) => this.handleChange(e,"userName")} />
                        </td>
                    </tr>
                    <tr>
                        <td>
                            User Surname :
                        </td>
                        <td>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.surname}
                                onChange={(e) => this.handleChange(e,"surname")}/>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            User Email :
                        </td>
                        <td>
                            <input type="text" className="form-control" value="" />
                        </td>
                    </tr>
                    <tr >
                        <td>
                            User Group :
                        </td>
                        <td>
                            <input type="text" className="form-control" value="" />
                        </td>
                    </tr>
                    <tr >
                        <td>
                            Subscribe To :
                        </td>
                        <td>
                            <input type="text" className="form-control" value="" />
                        </td>
                    </tr>
                </tbody>
            </table>
        );
    }
}

export default AddUser;
