import React, { Component } from 'react';
import $ from "jquery";
import axios from "axios"

class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
          username: "",
          password: "",
          loginErrors: ""
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({
          [event.target.name]: event.target.value
        });
      }

      handleSubmit(event) {
        const { users, username, password } = this.state;

        axios
          .post(
            "http://localhost:3000/users",
            {
              user: {
                name: username,
                password: password
              }
            },
            {credentials: 'include'},
            { withCredentials: true }
          )
          .then(response => {
            let name = users.map((value) => value).filter(value => value.name === userName && value.password);
            let userpass = users.map((value) => value.password).filter(value => value === password);
              this.props.handleLogin(name, userpass);
              this.props.history.push("/users");
          })
          .catch(error => {
            console.log("login error", error);
          });
        event.preventDefault();
      }

    componentDidMount() {
        let users = [];
        const url = "http://localhost:3000/users";

        fetch(url)
        .then(response => response.json())
            .then(res => {
            $.each(res.users, function(index, value) {
              users.push({
                id: value.id, 
                name: value.name 
              });
            });
            this.setState(() => ({users}));
        }) .catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
   }

  render() { 

        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="container">
                        <div className="row">
                            <div className="s-6 m-8 l-8 xl-8">
                                <input
                                type="text"
                                name="username"
                                className ="form-control"
                                placeholder="User Name"
                                value={this.state.name}
                                onChange={this.handleChange}
                                required/>
                            </div>
                        </div>
                        <div className="row">
                          <div className="s-6 m-8 l-8 xl-8">
                              <input
                              type="password"
                              name="password"
                              className ="form-control"
                              placeholder="Password"
                              value={this.state.password}
                              onChange={this.handleChange}
                              required/>  
                          </div>
                      </div>
                      <div className="row">
                          <button type="submit" className="btn btn-success">Submit</button>
                      </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Login;
