            name: value.name 
          });
        });
        this.setState(() => ({ users }));

    }).catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
}   */

  render() {
    let { users } = this.state;
    let user = "";
    if (users !== undefined) {
      user = users.map((value) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}> 
            </i>
            {this}
          </div>
        )
      });
    }
    return (
      <div className={CSS.loggeduser}>
        {user}
      </div>
    );
  }
}

export default Users;
