import React, { Component } from 'react';
import $ from "jquery";
import { LitElement, html } from 'lit-element';
import '@fortawesome/fontawesome-free/css/all.css';

const CSS = {
  loggeduser: 'loggeduser',
  menu: 'menu',
  profile: 'profile'
}

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: this.props.users,
      openMenu: false,
      userProfile: "hide",
      eventList: "show"
    };
    this.openMenu = this.openMenu.bind(this);
    this.userProfile = this.userProfile.bind(this);
    this.eventList = this.eventList.bind(this);
  }

  openMenu() {
    if (this.state.openMenu === false) {
      this.setState({ openMenu: !this.state.openMenu });
    }
    else {
      this.setState({ openMenu: !this.state.openMenu })
    }
  }

  userProfile() {
    if (this.state.userProfile === "hide") {
      this.setState({ userProfile: "show", openMenu: !this.state.openMenu });
    }
  }

  eventList() {
    if (this.state.eventList === "hide") {
      this.setState({ eventList: "show", userProfile: "hide", openMenu: !this.state.openMenu });
    }
  }
  /* componentDidMount() {
    let users = [];
    const url = "http://localhost:3000/users";

    fetch(url)
    .then(response => response.json())
      .then(res => {
          console.log(res.users)
        $.each(res.users, function(index, value) {
          users.push({
            id: value.id, 
            name: value.name 
          });
        });
        this.setState(() => ({ users }));

    }).catch(() => console.log("Canâ€™t access " + url + " response. Blocked by browser?"));
}   */

  render() {
    let { users } = this.state;
    let user, profile = "";

    if (users !== undefined) {
      user = users.map((value, index) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}>
            </i>
            {this.state.openMenu ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={this.userProfile}>UserProfile</li>
                <li key="eventlist" onClick={this.eventList}>Show Events</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
            {this.state.userProfile === "show" ?
              <table className="table table-borderless">
                <tbody>
                  <tr key={index + value.name} >
                    <td >
                      User Name :
                    </td>
                    <td>
                      <input type="text" className="form-control" value={value.name} readOnly />
                    </td>
                  </tr>
                  <tr key={index + value.surname} >
                    <td>
                      User Surname :
                    </td>
                    <td>
                      <input type="text" className="form-control" value={value.surname} readOnly />
                    </td>
                  </tr>
                  <tr key={index + value.Email} >
                    <td>
                      User Email :
                    </td>
                    <td>
                      <input type="text" className="form-control" value={value.Email} readOnly />
                    </td>
                  </tr>
                  <tr key={index + value.userGroup} >
                    <td>
                      User Group :
                    </td>
                    <td>
                      <input type="text" className="form-control" value={value.userGroup} readOnly />
                    </td>
                  </tr>
                  <tr key={index + value.subscribeto} >
                    <td>
                      User Subscribes To :
                    </td>
                    <td>
                      <input type="text" className="form-control" value={value.subscribeto} readOnly />
                    </td>
                  </tr>
                </tbody>
              </table> : null
            }
          </div>
        )
      });
    }


    return (
      <div className={CSS.loggeduser}>
        {user}
      </div>
    );
  }
}

export default Users;
