import React, { Component } from 'react';
import ReactDom from "react-dom";
import $ from "jquery";

class Login extends Component {
    state = {}

    componentDidMount() {
        let users = [];
        //const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const url = "http://localhost:3000";

        fetch(url)
        .then(response => response.json())
        .then(res => {
         console.log(res.users);
            $.each(res.users, function(index, value) {
              users.push({
                id: value.id, 
                name: value.name 
              });
            });
            this.setState(() => ({
              items: users
            }));
        })

      .catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
    }

    render() { 
        return (
            <div>
                <form method="POST" action="/users">
                    <div className="container">
                        <div className="row">
                            <div className="s-6 m-4 l-4 xl-4">
                                <label placeholder="Enter Username" required>User Name</label>
                            </div>
                            <div className="s-6 m-4 l-8 xl-8">
                                <input name= "username" className="form-control text-center" type="text" />
                            </div>
                        </div>
                        <div className="row">
                            <div className="s-6 m-4 l-4 xl-4">   
                                <label placeholder="Enter Password" required>Passowrd</label>
                            </div> 
                            <div className="s-6 m-4 l-8 xl-8">
                                <input name="userpass" className="form-control text-center" type="password" />  
                            </div>
                        </div>
                        <div className="row">
                            <button name="userform" className="btn btn-success">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Login;
