import React, { Component } from 'react';
import '@fortawesome/fontawesome-free/css/all.css';
import UserProfile from "./userProfile";
import Events from "./events";
import AdminUser from "./adminUser";
import AddUser from "./addUser";
import { findAllByTestId } from '@testing-library/react';

const CSS = {
  loggeduser: 'loggeduser',
  menu: 'menu',
  profile: 'profile'
}

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: this.props.users,
      userList: this.props.userList,
      handelDisplay : ['userProfile', 'eventList', 'manageUser', 'addUser'],
      openMenu: false,
      userProfile: "hide",
      eventList: "show",
      manageUser: "hide",
      addUser: "hide"
    };
    this.openMenu = this.openMenu.bind(this);
    this.userProfile = this.userProfile.bind(this);
    this.eventList = this.eventList.bind(this);
    this.manageUser = this.manageUser.bind(this);
    this.addUser = this.addUser.bind(this);
    this.changeDisplay = this.changeDisplay(this);
  }

  openMenu() {
    if (this.state.openMenu === false) {
      this.setState({ openMenu: !this.state.openMenu});
    }
    else {
      this.setState({openMenu: !this.state.openMenu})
    }
  }

  handelDisplay(text) {
    let { handelDisplay } = this.state;
    let showElement = text;
    handelDisplay.map((item) => {
      if (item.toLowerCase() === text.toLowerCase()) {
        return (
         this.setState({ s : "show"})


        )
      }
    })
  }

  userProfile() {
    if (this.state.userProfile === "hide") {
      this.setState({ userProfile: "show", openMenu: !this.state.openMenu , eventList: "hide", manageUser: "hide" , addUser:"hide"});
    }
  }

  eventList() {
    if (this.state.eventList === "hide") {
      this.setState({ eventList: "show", userProfile: "hide", openMenu: !this.state.openMenu , manageUser: "hide" , addUser:"hide"});
    }
  }

  manageUser() {
    if (this.state.manageUser === "hide") {
      this.setState({ manageUser: "show", eventList: "hide", userProfile: "hide", addUser:"hide"});
    }
  }
  addUser() {
    if (this.state.addUser === "hide") {
      this.setState({ addUser: "show", eventList: "hide", userProfile: "hide", manageUser: "hide"});
    }
  }

  render() {
    let { users, userList } = this.state;
    let user = "";

    if (users !== undefined) {
      user = users.map((value, index) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}>
            </i>
            {this.state.openMenu ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={() => this.handelDisplay("userProfile")}>UserProfile</li>
                <li key="eventlist" onClick={this.eventList}>Show Events</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
            {value.userGroup.toLowerCase() === "admin" ?
              <div className="container">
                <div className="row m-4">
                  <button
                    type="submit"
                    className="btn btn-success m-2"
                    onClick={this.manageUser}
                  >Manage User</button>
                   <button
                    type="submit"
                    className="btn btn-success m-2"
                    onClick={this.addUser}
                  >Add User</button>
                </div>
              </div> : null
            }
            {this.state.manageUser === "show" ?
                <AdminUser userList={userList}/> : null
            }
            {this.state.addUSer === "show" ?
                <AddUser userList={userList}/> : null
            }
            {this.state.userProfile === "show" ?
              <UserProfile value={value} index={index} /> : null
            }
            {this.state.eventList === "show" ?
              <Events value={value} index={index} /> : null
            }
          </div>
        )
      });
    }
    return (
      <div className={CSS.loggeduser}>
        {user}
      </div>
    );
  }
}

export default Users;
