import React, { Component } from 'react';
import '@fortawesome/fontawesome-free/css/all.css';
import UserProfile from "./userProfile";
import Events from "./events";
import AdminUser from "./adminUser";
import AddUser from "./addUser";
import { findAllByTestId } from '@testing-library/react';

const CSS = {
  loggeduser: 'loggeduser',
  menu: 'menu',
  profile: 'profile'
}

class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: this.props.users,
      userList: this.props.userList,
      openMenu: false,
      userProfile: "hide",
      eventList: "show",
      manageUser: "hide",
      addUser: "hide"
    };
    this.openMenu = this.openMenu.bind(this);
  }

  handleSubmit = (event) => {
    const { userName, surname, Email } = this.state;

    axios
        .post(
            "http://localhost:3000/users/:name",
            {
                name: userName,
                surname,
                Email,
                userGroup: $('#userGroup').val(),
                subscribeto: $('#subscribeTo').val()
            },
            { credentials: 'include' },
            { withCredentials: true }
        )
        .then(response => { })
        .catch(error => {
            console.log("login error", error);
        });
    event.preventDefault();
}

  openMenu() {
    if (this.state.openMenu === false) {
      this.setState({ openMenu: !this.state.openMenu });
    }
    else {
      this.setState({ openMenu: !this.state.openMenu })
    }
  }

  handelDisplay = (status) => {
    switch (status) {
      case "userProfile":
        this.setState({
          userProfile: "show",
          openMenu: !this.state.openMenu,
          eventList: "hide",
          manageUser: "hide",
          addUser: "hide"
        });
        break;
      case "eventList":
        this.setState({
          eventList: "show",
          userProfile: "hide",
          openMenu: !this.state.openMenu,
          manageUser: "hide",
          addUser: "hide"
        });
        break;
      case "manageUser":
        this.setState({
          manageUser: "show",
          eventList: "hide",
          userProfile: "hide",
          addUser: "hide"
        });
        break;
      case "addUser":
        this.setState({
          addUser: "show",
          eventList: "hide",
          userProfile: "hide",
          manageUser: "hide"
        });
        break;
    }
  }  

  render() {
    let { users, userList, userProfile, addUser, eventList, manageUser, openMenu } = this.state;
    let user = "";

    if (users !== undefined) {
      user = users.map((value, index) => {
        return (
          <div key={value.id}>
            Hallo {value.name} {" "}
            <i
              className="fas fa-angle-down m-2"
              color="#2980B9"
              size="2rem"
              onClick={this.openMenu}>
            </i>
            {openMenu ?
              <ul className={CSS.menu}>
                <li key="userprofile" onClick={(e) => this.handelDisplay('userProfile')}>UserProfile</li>
                <li key="eventlist" onClick={(e) => this.handelDisplay('eventLsit')}>Show Events</li>
                <li key="logout">Logout</li>
              </ul> : null
            }
            {value.userGroup.toLowerCase() === "admin" ?
              <div className="container">
                <div className="row m-4">
                  <button
                    type="submit"
                    className="btn btn-success m-2"
                    onClick={(e) => this.handelDisplay('manageUser')}
                  >Manage User</button>
                  <button
                    type="submit"
                    className="btn btn-success m-2"
                    onClick={(e) => this.handelDisplay('addUser')}
                  >Add User</button>
                </div>
              </div> : null
            }
            {manageUser === "show" ?
              <AdminUser userList={userList} /> : null
            }
            {addUser === "show" ?
              <AddUser
                userList={userList}
              on/> : null
            }
            {userProfile === "show" ?
              <UserProfile value={value} index={index} /> : null
            }
            {eventList === "show" ?
              <Events value={value} index={index} /> : null
            }
          </div>
        )
      });
    }
    return (
      <div className={CSS.loggeduser}>
        {user}
      </div>
    );
  }
}

export default Users;
