import React, { Component } from 'react';
import ReactDom from "react-dom";

class Login extends Component {
    state = {}

    componentDidMount() {
        let stations = [];
        //const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const url = "http://localhost:3000/users";

        fetch(url)
        .then(response => response.json())
        .then(res => {
         console.log(res.users);
            $.each(res.stations, function(index, value) {
              stations.push({
                label: value.fbez, //station name
                stop_number: value.stop_number // station id of the station name
              });
            });
            this.setState(() => ({
              items: stations
            }));
        })

      .catch(() => console.log("Can’t access " + url + " response. Blocked by browser?"));
    }

    render() { 
        return (
            <div>
                <form>
                    <div className="container">
                        <div className="row">
                            <div className="s-6 m-4 l-4 xl-4">
                                <label placeholder="Enter Username" required>User Name</label>
                            </div>
                            <div className="s-6 m-4 l-8 xl-8">
                                <input className="form-control text-center" type="text" />
                            </div>
                        </div>
                        <div className="row">
                            <div className="s-6 m-4 l-4 xl-4">   
                                <label placeholder="Enter Password" required>Passowrd</label>
                            </div> 
                            <div className="s-6 m-4 l-8 xl-8">
                                <input className="form-control text-center" type="password" />  
                            </div>
                        </div>
                        <div className="row">
                            <button className="btn btn-success">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Login;
