import React, { Component } from 'react';
import $ from "jquery";
import axios from "axios";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      loginErrors: ""
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  handleSubmit(event) {
    const { users, username, password } = this.state;

    axios
      .post(
        "http://localhost:3000/users",
        {
          user: {
            name: username,
            password: password
          }
        },
        { credentials: 'include' },
        { withCredentials: true }
      )
      .then(response => {
        let loginUser = users.map((value) => value).filter(value => value.name === username && value.password === password);
        let params = loginUser[0].name;
        this.props.handleLogin(loginUser, users);
        this.props.history.push("/users/" + params);
      })
      .catch(error => {
        console.log("login error", error);
      });
    event.preventDefault();
  }

  setValue = () => {    
    this.setState({ value });
  }

  state = {
    setValue: this.setValue,
    value: localStorage.getItem("parentValueKey")
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.value !== prevState.value) {
      // Whatever storage mechanism you end up deciding to use.
      localStorage.setItem("parentValueKey")
    }
  }

  componentDidMount() {
    let users = [];
    const url = "http://localhost:3000/users";

    fetch(url)
      .then(response => response.json())
      .then(res => {
        $.each(res.users, function (index, value) {
          users.push({
            id: value.id,
            name: value.name,
            surname: value.surname,
            password: value.password,
            Email: value.Email,
            userGroup: value.userGroup,
            subscribeTo: value.subscribeto
          });
        });
        this.setState(() => ({ users }));
      }).catch(() => console.log("Canâ€™t access " + url + " response. Blocked by browser?"));
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div className="container">
            <div className="row m-4">
              <input
                type="text"
                name="username"
                className="form-control"
                placeholder="User Name"
                value={this.state.name}
                onChange={this.handleChange}
                required />
            </div>
            <div className="row m-4">
              <input
                type="password"
                name="password"
                className="form-control"
                placeholder="Password"
                value={this.state.password}
                onChange={this.handleChange}
                required />
            </div>
            <div className="row m-4">
              <button type="submit" className="btn btn-success">Submit</button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default Login;
